import { motion } from 'framer-motion';
import clsx from 'clsx';
import { CheckCircle, AlertTriangle } from 'lucide-react';

const colorVariants = {
    cyan: {
        border: 'border-cyan-500',
        text: 'text-cyan-400',
        check: 'text-cyan-500',
        spinner: 'bg-cyan-400'
    },
    purple: {
        border: 'border-purple-500',
        text: 'text-purple-400',
        check: 'text-purple-500',
        spinner: 'bg-purple-400'
    },
    blue: {
        border: 'border-blue-500',
        text: 'text-blue-400',
        check: 'text-blue-500',
        spinner: 'bg-blue-400'
    },
    amber: {
        border: 'border-amber-500',
        text: 'text-amber-400',
        check: 'text-amber-500',
        spinner: 'bg-amber-400'
    },
    emerald: {
        border: 'border-emerald-500',
        text: 'text-emerald-400',
        check: 'text-emerald-500',
        spinner: 'bg-emerald-400'
    }
};

const AgentCard = ({ title, agentName, icon: Icon, status, content, delay = 0, color = "cyan", darkMode = true }) => {
    const isLoading = status === 'pending';
    const isDone = status === 'done';
    const styles = colorVariants[color] || colorVariants.cyan;

    const variants = {
        hidden: { opacity: 0, y: 20 },
        visible: { opacity: 1, y: 0, transition: { delay, duration: 0.5 } },
        loading: {
            opacity: 1, y: 0,
            boxShadow: ["0px 0px 0px rgba(6,182,212,0)", "0px 0px 20px rgba(6,182,212,0.2)", "0px 0px 0px rgba(6,182,212,0)"],
            borderColor: ["rgba(30,41,59,1)", "rgba(6,182,212,0.4)", "rgba(30,41,59,1)"],
            transition: { duration: 2, repeat: Infinity }
        }
    };

    const getSeverityColor = (sev) => {
        switch (sev) {
            case 'HIGH': return 'text-red-400';
            case 'MEDIUM': return 'text-yellow-400';
            default: return 'text-slate-400';
        }
    };

    return (
        <motion.div
            initial="hidden"
            animate={isLoading ? "loading" : "visible"}
            variants={variants}
            className={clsx(
                "p-5 border-l-4 h-full flex flex-col transition-all duration-500 rounded-2xl",
                darkMode ? "glass-card" : "bg-white border shadow-sm",
                isDone ? styles.border : darkMode ? "border-slate-700 opacity-70" : "border-slate-300 opacity-70",
                isLoading && (darkMode ? "shadow-[0_0_15px_-3px_rgba(6,182,212,0.15)] border-cyan-500/30 opacity-100" : "border-cyan-400 opacity-100")
            )}
        >
            <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-3">
                    <div className={clsx("p-2 rounded-lg", darkMode ? "bg-slate-800" : "bg-slate-100", isDone && styles.text)}>
                        <Icon size={20} />
                    </div>
                    <div>
                        <h3 className={clsx("font-semibold text-sm", darkMode ? "text-slate-200" : "text-slate-800")}>{title}</h3>
                        <p className="text-xs text-slate-500 uppercase tracking-wider">{agentName}</p>
                    </div>
                </div>
                {isLoading && <ActivityIndicator colorClass={styles.spinner} />}
                {isDone && <CheckCircle size={16} className={styles.check} />}
            </div>

            <div className={clsx("flex-1 text-sm space-y-2 overflow-y-auto max-h-60 custom-scrollbar", darkMode ? "text-slate-300" : "text-slate-700")}>
                {!content && isLoading && (
                    <div className="animate-pulse flex space-x-4">
                        <div className="flex-1 space-y-2 py-1">
                            <div className={clsx("h-2 rounded w-3/4", darkMode ? "bg-slate-700" : "bg-slate-200")}></div>
                            <div className={clsx("h-2 rounded", darkMode ? "bg-slate-700" : "bg-slate-200")}></div>
                        </div>
                    </div>
                )}

                {content && renderContent(title, content, getSeverityColor, darkMode)}
            </div>
        </motion.div>
    );
};

// Helper to render specific content based on agent type
const renderContent = (title, data, colorFn, darkMode = true) => {
    if (title === "Vision Scraper") {
        return (
            <div className="space-y-1">
                <div className="flex justify-between border-b border-slate-700 pb-1">
                    <span>Match</span>
                    <span className="font-mono text-white">{data.team_1} vs {data.team_2}</span>
                </div>
                <div className="flex justify-between">
                    <span>Time</span>
                    <span className="text-slate-400">{data.time}</span>
                </div>
                <div className="mt-2 bg-slate-800 p-2 rounded">
                    <p className="text-xs text-slate-500 mb-1">ODDS</p>
                    <div className="grid grid-cols-3 gap-2 text-center text-xs">
                        <div className="bg-slate-900 p-1 rounded">{data.odds?.team_1 || data.odds?.Team1 || data.odds?.home || 0}</div>
                        <div className="bg-slate-900 p-1 rounded">X: {data.odds?.draw || data.odds?.Draw || 0}</div>
                        <div className="bg-slate-900 p-1 rounded">{data.odds?.team_2 || data.odds?.Team2 || data.odds?.away || 0}</div>
                    </div>
                </div>
            </div>
        );
    }
    if (title === "Fact Checker") {
        return (
            <div className="space-y-3">
                {Object.entries(data.injuries || {}).map(([team, list]) => (
                    <div key={team}>
                        <span className="text-xs font-bold text-slate-500">{team} INJURIES</span>
                        <ul className="list-disc list-inside text-xs text-red-300">
                            {/* Handle string or array format gracefully */}
                            {Array.isArray(list) ? list.map((inj, i) => <li key={i}>{inj}</li>) : <li>{list}</li>}
                        </ul>
                    </div>
                ))}
                <div>
                    <span className="text-xs font-bold text-slate-500">FORM</span>
                    <div className="flex justify-between text-xs font-mono">
                        {Object.entries(data.form_last_5 || {}).map(([t, f]) => (
                            <span key={t}>{t.substring(0, 3)}: <span className="text-white">{f}</span></span>
                        ))}
                    </div>
                </div>
            </div>
        );
    }
    if (title === "Inside Intel") {
        return (
            <div className="space-y-3">
                {/* Grok Quality Warning */}
                {data.quality_warning && (
                    <div className="bg-amber-500/10 border border-amber-500/30 p-2 rounded flex gap-2 items-start">
                        <AlertTriangle size={14} className="text-amber-400 mt-0.5" />
                        <p className="text-xs text-amber-200">{data.quality_warning}</p>
                    </div>
                )}

                {data.psychological_flags?.map((flag, i) => (
                    <div key={i} className="flex gap-2 items-start bg-slate-800/50 p-2 rounded border border-red-900/30">
                        <AlertTriangle size={14} className={colorFn(flag.severity)} />
                        <div>
                            <p className="font-bold text-xs text-slate-300">{flag.player}</p>
                            <p className="text-xs text-slate-400">{flag.issue}</p>
                        </div>
                    </div>
                ))}
                {data.locker_room?.map((item, i) => (
                    <div key={i} className="text-xs text-slate-400 italic">
                        "{typeof item === 'object' ? item.issue || JSON.stringify(item) : item}"
                    </div>
                ))}
                {data.discipline && (
                    <div className="text-xs text-slate-400">
                        <span className="font-bold text-slate-500">DISCIPLINE:</span> {typeof data.discipline === 'object' ? JSON.stringify(data.discipline) : data.discipline}
                    </div>
                )}
                {/* Show raw_content if no structured data */}
                {data.raw_content && (
                    <div className="text-xs text-slate-400 bg-slate-800/50 p-2 rounded max-h-32 overflow-y-auto">
                        {data.raw_content}
                    </div>
                )}
                {(!data.psychological_flags?.length && !data.locker_room?.length && !data.raw_content && !data.discipline && !data.quality_warning) &&
                    <p className="text-slate-500 italic">No significant flags detected.</p>
                }
            </div>
        );
    }
    if (title === "Master Strategist") {
        const rec = data.recommendation || {};
        const math = data.mathematical_derivation || {};

        return (
            <div className="space-y-4">
                {/* Primary Recommendation */}
                <div className="text-center py-3 bg-gradient-to-r from-slate-900/50 to-slate-800/50 rounded-lg border border-slate-700/50">
                    <p className="text-[10px] text-slate-500 tracking-widest uppercase mb-1">OPTIMAL STRATEGY</p>
                    <p className="text-xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-emerald-400 to-cyan-400">
                        {rec.primary_bet || rec.main || "Analyzing..."}
                    </p>
                    <div className="flex justify-center items-center gap-3 mt-1">
                        <span className="bg-slate-800 px-2 py-0.5 rounded text-xs text-slate-300 border border-slate-700">
                            @{rec.odds || "-"}
                        </span>
                        <span className="bg-emerald-900/40 px-2 py-0.5 rounded text-xs text-emerald-400 border border-emerald-500/30">
                            {rec.stake_amount || (rec.stake_percentage ? `${rec.stake_percentage}%` : "-")}
                        </span>
                    </div>
                </div>

                {/* Mathematical Proof Section */}
                <div className="bg-slate-900/60 p-3 rounded-lg border border-slate-800 space-y-2">
                    <div className="flex items-center gap-2 mb-2">
                        <div className="w-1.5 h-1.5 rounded-full bg-cyan-500"></div>
                        <h4 className="text-xs font-bold text-slate-300 uppercase tracking-wider">Mathematical Proof</h4>
                    </div>

                    {/* Poisson & True Odds */}
                    <div className="grid grid-cols-2 gap-2 text-[10px]">
                        <div className="bg-slate-950 p-2 rounded border border-slate-800">
                            <p className="text-slate-500 mb-1">POISSON xG</p>
                            <div className="flex justify-between font-mono text-slate-300">
                                <span>Home: {math.poisson_xg?.team_1 || "-"}</span>
                                <span>Away: {math.poisson_xg?.team_2 || "-"}</span>
                            </div>
                        </div>
                        <div className="bg-slate-950 p-2 rounded border border-slate-800">
                            <p className="text-slate-500 mb-1">KELLY STAKE</p>
                            <div className="font-mono text-emerald-400">
                                {math.kelly_fractional || math.kelly_full || "-"}%
                                <span className="text-slate-600 ml-1">(Safety Cap Applied)</span>
                            </div>
                        </div>
                    </div>

                    <div className="text-[10px] text-slate-400 italic border-l-2 border-slate-800 pl-2">
                        "{math.market_odds_comparison || "Calculating true odds edge..."}"
                    </div>
                </div>

                {/* EV Analysis */}
                <div className="bg-slate-800/30 p-3 rounded text-xs border border-slate-700/50">
                    <span className="font-bold text-cyan-400">EV ANALYSIS:</span> {data.ev_analysis || "Processing..."}
                </div>

                {/* Deep Reasoning */}
                <div className="prose prose-invert prose-xs max-w-none text-slate-400/90 leading-relaxed">
                    {data.reasoning}
                </div>
            </div>
        );
    }
    return <pre className="text-xs overflow-x-auto">{JSON.stringify(data, null, 2)}</pre>;
};

const ActivityIndicator = ({ colorClass }) => (
    <div className={`flex gap-1`}>
        <span className={`w-1.5 h-1.5 rounded-full animate-bounce ${colorClass}`}></span>
        <span className={`w-1.5 h-1.5 rounded-full animate-bounce delay-75 ${colorClass}`}></span>
        <span className={`w-1.5 h-1.5 rounded-full animate-bounce delay-150 ${colorClass}`}></span>
    </div>
);

export default AgentCard;

import { Eye, BookOpen, UserRound, Brain } from 'lucide-react';
import AgentCard from './AgentCard';

const Blackboard = ({ state, darkMode }) => {
    const isBlank = !state || (!state.MATCH_DATA && !state.FACT_REPORT && !state.INSIDER_INTEL && !state.STRATEGY_REPORT);

    if (isBlank) return (
        <div className={`h-full flex items-center justify-center p-12 rounded-2xl border ${darkMode ? 'glass-card text-slate-500' : 'bg-white border-slate-200 text-slate-400'}`}>
            <div className="text-center">
                <Brain size={48} className="mx-auto mb-4 opacity-20" />
                <p>Waiting for analysis trigger...</p>
            </div>
        </div>
    );

    return (
        <div className="flex flex-col gap-6 h-full">
            {/* Top Row: Data Agents */}
            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
                <AgentCard
                    title="Vision Scraper"
                    agentName="GPT-5.2 Vision"
                    icon={Eye}
                    status={state.MATCH_DATA ? 'done' : 'pending'}
                    content={state.MATCH_DATA}
                    color="cyan"
                    delay={0.1}
                    darkMode={darkMode}
                />
                <AgentCard
                    title="Fact Checker"
                    agentName="Perplexity Sonar Pro"
                    icon={BookOpen}
                    status={state.FACT_REPORT ? 'done' : 'pending'}
                    content={state.FACT_REPORT}
                    color="blue"
                    delay={0.3}
                    darkMode={darkMode}
                />
                <AgentCard
                    title="Inside Intel"
                    agentName="Grok 4 Fast"
                    icon={UserRound}
                    status={state.INSIDER_INTEL ? 'done' : 'pending'}
                    content={state.INSIDER_INTEL}
                    color="purple"
                    delay={0.5}
                    darkMode={darkMode}
                />
            </div>

            {/* Bottom Section: Strategy */}
            <div className="flex-1 min-h-[300px]">
                <AgentCard
                    title="Master Strategist"
                    agentName="GPT-5.2 (PhD Edition)"
                    icon={Brain}
                    status={state.STRATEGY_REPORT ? 'done' : 'pending'}
                    content={state.STRATEGY_REPORT}
                    color="emerald"
                    delay={0.8}
                    darkMode={darkMode}
                />
            </div>
        </div>
    );
};

export default Blackboard;

import { useState, useEffect } from 'react';
import { X, Key, Save, CheckCircle, ExternalLink, Cpu, Plus } from 'lucide-react';

const SettingsModal = ({ isOpen, onClose, apiKeys, setApiKeys, modelSettings, setModelSettings, darkMode = true }) => {
    const [localKeys, setLocalKeys] = useState(apiKeys);
    // Add default grok model to state
    const [localModels, setLocalModels] = useState(modelSettings || { openai: 'gpt-5.2', perplexity: 'sonar-pro', grok: 'grok-4.1-fast' });
    const [saved, setSaved] = useState(false);

    // Custom model input states
    const [showCustomOpenai, setShowCustomOpenai] = useState(false);
    const [showCustomPerplexity, setShowCustomPerplexity] = useState(false);
    const [showCustomGrok, setShowCustomGrok] = useState(false);

    const [customOpenai, setCustomOpenai] = useState('');
    const [customPerplexity, setCustomPerplexity] = useState('');
    const [customGrok, setCustomGrok] = useState('');

    // User-added custom models (persisted)
    const [customModels, setCustomModels] = useState({ openai: [], perplexity: [], grok: [] });

    useEffect(() => {
        const storedKeys = localStorage.getItem('phd_betting_api_keys');
        const storedModels = localStorage.getItem('phd_betting_models');
        const storedCustomModels = localStorage.getItem('phd_betting_custom_models');
        if (storedKeys) {
            const parsed = JSON.parse(storedKeys);
            setLocalKeys(parsed);
            setApiKeys(parsed);
        }
        if (storedModels && setModelSettings) {
            const parsed = JSON.parse(storedModels);
            setLocalModels(prev => ({ ...prev, ...parsed }));
            setModelSettings(parsed);
        }
        if (storedCustomModels) {
            setCustomModels(JSON.parse(storedCustomModels));
        }
    }, [setApiKeys, setModelSettings]);

    const handleSave = () => {
        localStorage.setItem('phd_betting_api_keys', JSON.stringify(localKeys));
        localStorage.setItem('phd_betting_models', JSON.stringify(localModels));
        localStorage.setItem('phd_betting_custom_models', JSON.stringify(customModels));
        setApiKeys(localKeys);
        if (setModelSettings) setModelSettings(localModels);
        setSaved(true);
        setTimeout(() => {
            setSaved(false);
            onClose();
        }, 800);
    };

    const addCustomModel = (provider) => {
        let value;
        if (provider === 'openai') value = customOpenai.trim();
        else if (provider === 'perplexity') value = customPerplexity.trim();
        else if (provider === 'grok') value = customGrok.trim();

        if (!value) return;

        // Add to custom models list
        setCustomModels(prev => ({
            ...prev,
            [provider]: [...(prev[provider] || []), value]
        }));

        // Select it immediately
        setLocalModels(prev => ({ ...prev, [provider]: value }));

        // Clear input and hide
        if (provider === 'openai') {
            setCustomOpenai('');
            setShowCustomOpenai(false);
        } else if (provider === 'perplexity') {
            setCustomPerplexity('');
            setShowCustomPerplexity(false);
        } else if (provider === 'grok') {
            setCustomGrok('');
            setShowCustomGrok(false);
        }
    };

    if (!isOpen) return null;

    const apiProviders = [
        { key: 'openai', label: 'OpenAI', hint: 'Vision & Strategy (GPT-5.2)', placeholder: 'sk-...', link: 'https://platform.openai.com/api-keys', color: 'cyan' },
        { key: 'perplexity', label: 'Perplexity', hint: 'Fact Checker (Sonar Pro)', placeholder: 'pplx-...', link: 'https://docs.perplexity.ai/guides/getting-started', color: 'blue' },
        { key: 'grok', label: 'xAI (Grok)', hint: 'Insider Intel (Social Scanner)', placeholder: 'xai-...', link: 'https://console.x.ai/', color: 'purple' },
    ];

    // Expanded model lists based on user's screenshots
    const baseModelOptions = {
        openai: [
            'gpt-5.2',
            'gpt-5.2-pro',
            'gpt-5.1',
            'gpt-4.1',
            'gpt-4.1-mini',
            'gpt-4o',
            'gpt-4o-mini',
            'o4-mini',
            'o3',
            'o3-mini',
            'o3-pro',
        ],
        perplexity: [
            'sonar-pro',
            'sonar',
            'sonar-reasoning',
            'sonar-reasoning-pro',
            'sonar-deep-research',
            'sonar-deep-research-pro',
            'llama-3.1-sonar-large-128k-online',
        ],
        grok: [
            'grok-4.1-fast',
            'grok-4.1',
            'grok-4.1-thinking',
            'grok-4-fast',
            'grok-4-heavy',
            'grok-4',
            'grok-beta',
        ]
    };

    // Merge base + custom models
    const modelOptions = {
        openai: [...baseModelOptions.openai, ...customModels.openai],
        perplexity: [...baseModelOptions.perplexity, ...customModels.perplexity],
        grok: [...baseModelOptions.grok, ...(customModels.grok || [])]
    };

    // Dynamic classes for light/dark mode
    const modalBg = darkMode ? 'bg-slate-900/95 border-slate-700' : 'bg-white border-slate-200';
    const inputBg = darkMode ? 'bg-slate-800 border-slate-700 text-white placeholder-slate-500' : 'bg-slate-50 border-slate-300 text-slate-900 placeholder-slate-400';
    const labelColor = darkMode ? 'text-slate-400' : 'text-slate-600';
    const headerColor = darkMode ? 'text-white' : 'text-slate-900';
    const bannerBg = darkMode ? 'bg-slate-800/50 border-slate-700 text-slate-300' : 'bg-blue-50 border-blue-200 text-blue-800';
    const dividerColor = darkMode ? 'border-slate-700' : 'border-slate-200';
    const buttonSecondary = darkMode ? 'bg-slate-700 hover:bg-slate-600 text-white' : 'bg-slate-200 hover:bg-slate-300 text-slate-700';

    return (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-md z-50 flex items-center justify-center p-4">
            <div className={`p-6 w-full max-w-lg relative animate-fade-in rounded-2xl border shadow-2xl max-h-[90vh] overflow-y-auto ${modalBg}`}>
                {/* Header */}
                <div className="flex items-center justify-between mb-6">
                    <div className="flex items-center gap-3">
                        <div className="p-2 rounded-lg bg-gradient-to-br from-cyan-500/20 to-blue-500/20">
                            <Key size={20} className="text-cyan-500" />
                        </div>
                        <h3 className={`text-xl font-bold ${headerColor}`}>API Configuration</h3>
                    </div>
                    <button onClick={onClose} className={`p-2 rounded-lg transition ${darkMode ? 'hover:bg-slate-800 text-slate-400 hover:text-white' : 'hover:bg-slate-100 text-slate-500 hover:text-slate-900'}`}>
                        <X size={20} />
                    </button>
                </div>

                {/* Info Banner */}
                <div className={`border rounded-lg p-3 mb-6 text-sm ${bannerBg}`}>
                    <p>üîê Your keys are stored locally in your browser. Never sent to any server except the AI providers.</p>
                </div>

                {/* API Key Inputs */}
                <div className="space-y-4">
                    {apiProviders.map((provider) => (
                        <div key={provider.key}>
                            <div className="flex items-center justify-between mb-1.5">
                                <label className={`text-xs font-medium ${labelColor}`}>
                                    {provider.label} <span className={`text-${provider.color}-500`}>({provider.hint})</span>
                                </label>
                                <a href={provider.link} target="_blank" rel="noopener noreferrer" className={`text-xs flex items-center gap-1 text-${provider.color}-500 hover:underline`}>
                                    Get Key <ExternalLink size={12} />
                                </a>
                            </div>
                            <input
                                type="password"
                                value={localKeys[provider.key]}
                                onChange={e => setLocalKeys({ ...localKeys, [provider.key]: e.target.value })}
                                className={`w-full border rounded-lg p-2.5 text-sm transition outline-none focus:ring-2 focus:ring-cyan-500/50 ${inputBg}`}
                                placeholder={provider.placeholder}
                            />
                        </div>
                    ))}
                </div>

                {/* Model Selection */}
                <div className={`mt-6 pt-6 border-t ${dividerColor}`}>
                    <div className="flex items-center gap-2 mb-4">
                        <Cpu size={18} className="text-purple-500" />
                        <h4 className={`font-semibold text-sm ${headerColor}`}>Optional: Model Override</h4>
                    </div>

                    {/* OpenAI Model */}
                    <div className="mb-4">
                        <label className={`text-xs font-medium block mb-1.5 ${labelColor}`}>OpenAI Model</label>
                        <div className="flex gap-2">
                            <select
                                value={localModels.openai}
                                onChange={e => setLocalModels({ ...localModels, openai: e.target.value })}
                                className={`flex-1 border rounded-lg p-2.5 text-sm transition outline-none focus:ring-2 focus:ring-cyan-500/50 ${inputBg}`}
                            >
                                {modelOptions.openai.map(m => <option key={m} value={m}>{m}</option>)}
                            </select>
                            <button
                                onClick={() => setShowCustomOpenai(!showCustomOpenai)}
                                className={`px-3 rounded-lg transition ${buttonSecondary}`}
                                title="Add custom model"
                            >
                                <Plus size={18} />
                            </button>
                        </div>
                        {showCustomOpenai && (
                            <div className="mt-2 flex gap-2 animate-fade-in">
                                <input
                                    type="text"
                                    value={customOpenai}
                                    onChange={e => setCustomOpenai(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && addCustomModel('openai')}
                                    className={`flex-1 border rounded-lg p-2 text-sm transition outline-none focus:ring-2 focus:ring-cyan-500/50 ${inputBg}`}
                                    placeholder="Enter custom model name..."
                                    autoFocus
                                />
                                <button
                                    onClick={() => addCustomModel('openai')}
                                    className="px-4 py-2 rounded-lg bg-cyan-600 hover:bg-cyan-500 text-white text-sm font-medium transition"
                                >
                                    Add
                                </button>
                            </div>
                        )}
                    </div>

                    {/* Perplexity Model */}
                    <div className="mb-4">
                        <label className={`text-xs font-medium block mb-1.5 ${labelColor}`}>Perplexity Model</label>
                        <div className="flex gap-2">
                            <select
                                value={localModels.perplexity}
                                onChange={e => setLocalModels({ ...localModels, perplexity: e.target.value })}
                                className={`flex-1 border rounded-lg p-2.5 text-sm transition outline-none focus:ring-2 focus:ring-cyan-500/50 ${inputBg}`}
                            >
                                {modelOptions.perplexity.map(m => <option key={m} value={m}>{m}</option>)}
                            </select>
                            <button
                                onClick={() => setShowCustomPerplexity(!showCustomPerplexity)}
                                className={`px-3 rounded-lg transition ${buttonSecondary}`}
                                title="Add custom model"
                            >
                                <Plus size={18} />
                            </button>
                        </div>
                        {showCustomPerplexity && (
                            <div className="mt-2 flex gap-2 animate-fade-in">
                                <input
                                    type="text"
                                    value={customPerplexity}
                                    onChange={e => setCustomPerplexity(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && addCustomModel('perplexity')}
                                    className={`flex-1 border rounded-lg p-2 text-sm transition outline-none focus:ring-2 focus:ring-cyan-500/50 ${inputBg}`}
                                    placeholder="Enter custom model name..."
                                    autoFocus
                                />
                                <button
                                    onClick={() => addCustomModel('perplexity')}
                                    className="px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-500 text-white text-sm font-medium transition"
                                >
                                    Add
                                </button>
                            </div>
                        )}
                    </div>

                    {/* Grok Model */}
                    <div>
                        <label className={`text-xs font-medium block mb-1.5 ${labelColor}`}>xAI (Grok) Model</label>
                        <div className="flex gap-2">
                            <select
                                value={localModels.grok}
                                onChange={e => setLocalModels({ ...localModels, grok: e.target.value })}
                                className={`flex-1 border rounded-lg p-2.5 text-sm transition outline-none focus:ring-2 focus:ring-cyan-500/50 ${inputBg}`}
                            >
                                {modelOptions.grok.map(m => <option key={m} value={m}>{m}</option>)}
                            </select>
                            <button
                                onClick={() => setShowCustomGrok(!showCustomGrok)}
                                className={`px-3 rounded-lg transition ${buttonSecondary}`}
                                title="Add custom model"
                            >
                                <Plus size={18} />
                            </button>
                        </div>
                        {showCustomGrok && (
                            <div className="mt-2 flex gap-2 animate-fade-in">
                                <input
                                    type="text"
                                    value={customGrok}
                                    onChange={e => setCustomGrok(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && addCustomModel('grok')}
                                    className={`flex-1 border rounded-lg p-2 text-sm transition outline-none focus:ring-2 focus:ring-cyan-500/50 ${inputBg}`}
                                    placeholder="Enter custom model name..."
                                    autoFocus
                                />
                                <button
                                    onClick={() => addCustomModel('grok')}
                                    className="px-4 py-2 rounded-lg bg-purple-600 hover:bg-purple-500 text-white text-sm font-medium transition"
                                >
                                    Add
                                </button>
                            </div>
                        )}
                    </div>

                    {/* Show custom models if any */}
                    {(customModels.openai.length > 0 || customModels.perplexity.length > 0 || (customModels.grok && customModels.grok.length > 0)) && (
                        <p className={`text-xs mt-3 ${darkMode ? 'text-slate-500' : 'text-slate-400'}`}>
                            üí° Custom models: {[...customModels.openai, ...customModels.perplexity, ...(customModels.grok || [])].join(', ')}
                        </p>
                    )}
                </div>

                {/* Save Button */}
                <button
                    onClick={handleSave}
                    className={`w-full mt-6 flex items-center justify-center gap-2 py-3 rounded-lg font-bold transition-all ${saved
                        ? 'bg-emerald-600 text-white'
                        : 'bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500 text-white shadow-lg shadow-cyan-900/20'
                        }`}
                >
                    {saved ? <CheckCircle size={18} /> : <Save size={18} />}
                    {saved ? 'Saved!' : 'Save & Close'}
                </button>

                <p className={`text-xs text-center mt-4 ${darkMode ? 'text-slate-500' : 'text-slate-400'}`}>
                    Leave API keys empty to run in Simulation Mode (demo data).
                </p>
            </div>
        </div>
    );
};

